import React, {useEffect, useState} from 'react';
import { render } from 'react-dom';
import { View, Button, Image, TouchableOpacity, LinearLayout, Text, StyleSheet, ScrollView, Dimensions, FlexBox} from 'react-native';
import { withOrientation } from 'react-navigation';
import { Ionicons, MaterialIcons, MaterialCommunityIcons } from '@expo/vector-icons';
import { Audio } from 'expo-av'
var menus=[];
var categorys=[];
var sumPrice = 0;

export function VoiceApp(){
  const [recording, setRecording] = React.useState();
  const [printText, setPrintText] = React.useState('우측 버튼을 누르고 말하세요.');
  

  async function startRecording() {
    try {
      console.log('Requesting permissions..');
      await Audio.requestPermissionsAsync();
      await Audio.setAudioModeAsync({
        allowsRecordingIOS: true,
        playsInSilentModeIOS: true,
      }); 
      console.log('Starting recording..');
      const recording = new Audio.Recording();
      await recording.prepareToRecordAsync(Audio.RECORDING_OPTIONS_PRESET_HIGH_QUALITY);
      await recording.startAsync(); 
      setRecording(recording);
      setPrintText('듣고 있습니다.');
      console.log('Recording started');
    } catch (err) {
      stopRecording();
    }
  }

  async function stopRecording() {
    console.log('Stopping recording..');
    setRecording(undefined);
    setPrintText('우측 마이크 버튼을 누른 후 말하세요.');
    await recording.stopAndUnloadAsync();
    const uri = recording.getURI(); 
    console.log('Recording stopped and stored at', uri);
  }
  

  return (
    <View flexDirection='row' style={{width:'65%', alignItems:'center'}}>
      {recording ? 
      <Text style={{fontSize:24, color:'rgb(255,45,85)', marginLeft:10}}>{printText}</Text>: 
      <Text style={{fontSize:24, marginLeft:10}}>{printText}</Text>}
      <TouchableOpacity
        onPress={recording ? stopRecording : startRecording}
        style={[styles.button,{backgroundColor:'rgb(255,45,85)', marginLeft:'auto'}]}
      >
        {recording ? <Text style={{padding:0, color:'white', textAlign:'center',marginTop:'auto', marginBottom:'auto'}}><Ionicons name="ios-stop-sharp" style={{fontSize:24}}></Ionicons></Text>
         : <Text style={{padding:0, color:'white', textAlign:'center',marginTop:'auto', marginBottom:'auto'}}><Ionicons name="ios-mic" style={{fontSize:24}}></Ionicons></Text>}
      </TouchableOpacity>
    </View>
  );
}


class Home extends React.Component{
  
  constructor(props){
    super(props);
    this.onPressCat = this.onPressCat.bind(this);
    menus = [
      [
        {
          id: 1,
          img: '',
          menu: '돈까스',
          price:7000
        },
        {
          id: 2,
          img: '',
          menu: '치즈돈까스',
          price:9500
        },
        {
          id: 3,
          img: '',
          menu: '매운돈까스',
          price:8000
        }
      ],
      [
        {
          id: 1,
          img: '',
          menu: '사이다',
          price:1000
        },
        {
          id: 2,
          img: '',
          menu: '콜라',
          price:1000
        },
        {
          id: 3,
          img: '',
          menu: '이동준',
          price:12345
        },
    
        {
          id: 4,
          img: '',
          menu: '이동준1',
          price:67890
        }
      ]
    ]
    
    categorys = [
      '식사류', '음료'
    ]
  }

  componentDidMount(){
    console.log(this.getNavigationParams());
  }
  getNavigationParams(){
    let id = false;
    if(this.props.navigation.state.params) {
      id = this.props.navigation.state.params.itemId;
    }
    return id;
  }
  state = { category: 0,
    cart: [],
    recording: undefined,
    printText: '우측 마이크 버튼을 누르고 말하세요.',
  };

  startRecording = async() => {
    try {
      console.log('Requesting permissions..');
      await Audio.requestPermissionsAsync();
      await Audio.setAudioModeAsync({
        allowsRecordingIOS: true,
        playsInSilentModeIOS: true,
      }); 
      console.log('Starting recording..');
      this.setState({
        recording: new Audio.Recording()
      })
      await this.state.recording.prepareToRecordAsync(Audio.RECORDING_OPTIONS_PRESET_HIGH_QUALITY);
      await this.state.recording.startAsync(); 
      this.setState({
        recording : this.state.recording,
        printText: '듣고 있습니다.'
      });
      console.log('Recording started');
    } catch (err) {
      stopRecording();
    }
  }

  stopRecording = async() => {
    console.log('Stopping recording..');
    console.log(this.state.recording);
    this.setState({
      recording : undefined,
      printText: '우측 마이크 버튼을 누른 후 말하세요.'
    });
    await this.state.recording.stopAndUnloadAsync();
    const uri = this.state.recording['_uri']; 
    console.log('Recording stopped and stored at', uri);
  }

  printMenus(menu){
    
    if(menu.id%2==0) {
    return;
    }
    else{
      return (
        <TouchableOpacity style={styles.menu} onPress={()=>{this.onPressMenu(menu.menu, menu.price)}}>
         <Image 
          style={{width:'100%', height:200}}
          source={{url:'data:image/jpeg;base64,'}}/>
        <View>
          <Text style={{fontSize:32, padding:5}}>{menu.menu}</Text>
        </View>
        <View>
          <Text style={{fontSize:24, padding:5}}>{menu.price}원</Text>
        </View>
      </TouchableOpacity>
      );
    }
  }
  printMenus2(menu){
    if(menu.id%2==0) {
    return (
      <TouchableOpacity style={styles.menu} onPress={()=>{this.onPressMenu(menu.menu, menu.price)}}>
        <Image 
          style={{width:'100%', height:200}}
          source={{url:'data:image/jpeg;base64,'}}/>
        <View>
          <Text style={{fontSize:32, padding:5}}>{menu.menu}</Text>
        </View>
        <View>
          <Text style={{fontSize:24, padding:5}}>{menu.price}원</Text>
        </View>
      </TouchableOpacity>
          );
    }
    else{
      return;
    }
  }

  onPressMenu(menu, price){
    var tempCart = this.state.cart;
    this.setState({cart:this.isDup(tempCart, menu, price)})
  }

  isDup(cart, menu, price){
    for(var i=0;i<cart.length;i++){
      if(cart[i].menu == menu){
        cart[i].qty += 1;
        return cart;
      }
    }
    cart.push({
      menu: menu,
      qty: 1,
      price: price
      });
    return cart;
  }

  onPressCat(index){
    this.setState({
      category:index,
    }
    )
  }
  buttonRenderer(){
    var temp = categorys.map((category, index)=>(
      <View>
        <TouchableOpacity onPress={()=>{this.onPressCat(index)}} style={{padding:10}}><Text style={{fontSize:32}}>{category}</Text></TouchableOpacity>
      </View>
    ));
    return temp;
  }
  
  sumPriceHandler(){
    var cart = this.state.cart;
    var sum = 0;
    for(var i=0;i<cart.length;i++){
      sum += cart[i].price * cart[i].qty;
    }
    return sum;
  }
  onPressDel(menu){
    this.popMenu(menu);
  }
  popMenu(menu){
    console.log(menu);
    var cart = this.state.cart;
    var cart2 = [];
    for(var i=0;i<cart.length;i++){
      if(cart[i].menu == menu){
        continue;
      }
      cart2.push(cart[i]);
    }
    this.setState({cart:cart2});
  }

  handleSubmit(){
    if(this.state.cart.length!=0){
      console.log(this.state.cart);
      //서버 전송 구현
    }
    else
      console.log('pass');
  }
  handleVoiceSubmit(){
    if(this.state.cart.length!=0){
      console.log(this.state.cart);
      //서버 전송 구현
    }
    else
      console.log('voice pass');
  }
  handleRecord(){
    this.startRecording();
  }

  handleStopRecord(){
    this.stopRecording();
  }
  onPressPlus(menu){
    var cart = this.state.cart;
    for(var i=0;i<cart.length;i++){
      if(cart[i].menu == menu){
        cart[i].qty += 1;
        this.setState({cart:cart});
        break;
      }
    }
  }
  onPressMinus(menu){
    var cart = this.state.cart;
    for(var i=0;i<cart.length;i++){
      if(cart[i].menu == menu){
        if(cart[i].qty>1){
          cart[i].qty -= 1;
          this.setState({cart:cart});
          break;
        } else{
          this.popMenu(menu);
          break;
        }
      }
    }
  }
  
  render() {
    return(
      <View style={{flex:1}}>
        <View style={styles.header}>
          {this.buttonRenderer()}
        </View>
          <ScrollView stickyHeaderIndices={[1]} style={{flex: 1, backgroundColor: 'rgb(242, 242, 247)'}}>

            <View>
              <View style={styles.menuContainer}>
                {menus[this.state.category].map(menu=>(
                  this.printMenus(menu)
                ))}
              </View>
              <View style={styles.menuContainer}>
                {menus[this.state.category].map(menu=>(
                  this.printMenus2(menu)
                ))}
              </View>
            </View>
          </ScrollView>
          <ScrollView style={styles.order}>
              <View style={{margin:10}}><Text style={{textAlign:'center', fontSize: 24}}>장바구니</Text></View>
              <View style={{minHeight:'73%'}}>
              {this.state.cart.map(cart=>(
                <View style={{margin:10}}>
                  <Text style={{fontSize:30}}>{cart.menu} <TouchableOpacity onPress={()=>this.onPressDel(cart.menu)}><Text style={{fontSize:20, color:'red', lineHeight:30}}>X</Text></TouchableOpacity></Text>
                  <Text style={{fontSize:20, marginLeft:'auto'}}><TouchableOpacity onPress={()=>this.onPressMinus(cart.menu)}><Text style={{fontSize:20}}>-</Text></TouchableOpacity> {cart.qty}개 <TouchableOpacity onPress={()=>this.onPressPlus(cart.menu)}><Text style={{fontSize:20}}>+</Text></TouchableOpacity> <Text style={{width:100}}>{cart.qty*cart.price}원</Text></Text>
                </View>
              ))}
              </View>
              <View style={styles.orderButtonContainer}>
                <Text style={{fontSize:30, textAlign:'right', margin:10}}> · {this.sumPriceHandler()}원</Text>
                <TouchableOpacity onPress={()=>this.handleSubmit()} style={styles.button}><Text style={{color:'white',fontSize:30, textAlign:'center',}}>주문하기</Text></TouchableOpacity>
              </View>
            </ScrollView>
           {/* <VoiceApp /> */}
           <View flexDirection='row' style={{width:'65%', alignItems:'center'}}>
            {this.state.recording ? 
            <Text style={{fontSize:24, color:'rgb(255,45,85)', marginLeft:10}}>{this.state.printText}</Text>: 
            <Text style={{fontSize:24, marginLeft:10}}>{this.state.printText}</Text>}
            <TouchableOpacity
              onPress={this.state.recording ? ()=>this.stopRecording() : ()=>this.startRecording()}
              style={[styles.button,{backgroundColor:'rgb(255,45,85)', marginLeft:'auto'}]}
            >
              {this.state.recording ? <Text style={{padding:0, color:'white', textAlign:'center',marginTop:'auto', marginBottom:'auto'}}><Ionicons name="ios-stop-sharp" style={{fontSize:24}}></Ionicons></Text>
              : <Text style={{padding:0, color:'white', textAlign:'center',marginTop:'auto', marginBottom:'auto'}}><Ionicons name="ios-mic" style={{fontSize:24}}></Ionicons></Text>}
            </TouchableOpacity>
            </View>
          </View>
    );
  }
}
const chartHeight = Dimensions.get('window').height;
const styles = StyleSheet.create({
  button:{
    borderWidth: 0,
    borderRadius:10,
    padding:10,
    margin:10,
    backgroundColor:'rgb(0, 122, 255)',
  },
  order:{
    backgroundColor: 'white',
    height: chartHeight,
    width: '35%',
    top: 0,
    marginTop:0, 
    right:1,
    left:'65%',
    flex: 1,
    borderRadius: 5,
    position: 'absolute'
  },
  menu:{
    backgroundColor: 'white',
    margin:10,
    borderRadius: 5,
    width: '47%',
    height:300
  },
  menuContainer:{
    flexDirection:'row',
    flex: 2,
    justifyContent: 'flex-start',
    alignItems: 'center',
    width:'61%',
    marginLeft:15,
    overflow: 'scroll'
  },
  orderButtonContainer:{
    marginTop:20,
    marginBottom:20,
  },
  header:{
    flexDirection:'row'
  }
});

export default Home;
